func BenchmarkClient(b *testing.B) {
	comm := tars.NewCommunicator()
	obj := "TestApp.TestServer.HelloObj@tcp -h 127.0.0.1 -p 50000 -t 60000"
	app := new(Hello)
	comm.StringToProxy(obj, app)
	var req = ""
	var out string

	for i := 0; i < b.N; i++ {
		_, err := app.TestHello(req, &out)
		if err != nil {
			fmt.Println(err)
			return
		}
	}
}
